def restore_array_from_queries(n, q, a):
    l = len(a)
    zeros = []
    last = {}
    cur_max = 1
    last_max = 1
    stack = []
    for i in range(l - 1, -1, -1):
        if a[i] == 0:
            zeros.append(i)
        elif a[i] not in last:
            last[a[i]] = i
    for i in range(l):
        if a[i] == 0:
            a[i] = max(cur_max, 1)
        elif a[i] > cur_max and last[a[i]] != i:
            stack.append(cur_max)
            cur_max = a[i]
        elif cur_max != 0 and i == last[cur_max]:
            cur_max = stack.pop()
        elif a[i] < cur_max:
            return ('NO', [])
    if q > max(a):
        if zeros:
            a[zeros[0]] = q
            return ('YES', a)
        else:
            return ('NO', [])
    elif q == max(a):
        return ('YES', a)
    elif q < max(a):
        return ('NO', [])

