def count_word_occurrences(words):
    word_count_dict = {}
    order_list = []
    for word in words:
        if word not in word_count_dict:
            word_count_dict[word] = 0
            order_list.append(word)
        word_count_dict[word] -= 1
    distinct_count = len(word_count_dict)
    occurrences = [word_count_dict[word] for word in order_list]
    return (distinct_count, occurrences)

