import math

def minMoves(A, N, K):
    count = 1
    for i in range(1, N):
        if A[i] > A[i - 1]:
            diff = A[i] - A[i - 1]
            div = math.ceil(diff / K)
            count += div
            A[i] = A[i] - K * div
    return count % 1000000007

