def longest_zero_sequence_after_repetition(k: int, st: str) -> int:
    if int(st) >= 0:
        s = st * k
        ans = 0
        temp = 0
        s = str(s)
        r = min(2, k)
        for i in range(len(st) * r):
            if s[i] == '0':
                temp += 1
            else:
                ans = max(ans, temp)
                temp = 0
        return ans
    else:
        return len(st) * k

