def calculate_intersection_volume(M, N, P, Q):

    def calc(x):
        y_ma = -100
        y_mi = 100
        for i in range(M):
            (x0, y0) = P[i - 1]
            (x1, y1) = P[i]
            if x0 <= x <= x1 or x1 <= x <= x0:
                if x0 == x1:
                    y_ma = max(y_ma, max(y1, y0))
                    y_mi = min(y_mi, min(y1, y0))
                else:
                    y = (x - x0) * (y1 - y0) / (x1 - x0) + y0
                    y_ma = max(y_ma, y)
                    y_mi = min(y_mi, y)
        if not y_mi <= y_ma:
            return 0
        z_ma = -100
        z_mi = 100
        for i in range(N):
            (x0, z0) = Q[i - 1]
            (x1, z1) = Q[i]
            if x0 <= x <= x1 or x1 <= x <= x0:
                if x0 == x1:
                    z_ma = max(z_ma, max(z1, z0))
                    z_mi = min(z_mi, min(z1, z0))
                else:
                    z = (x - x0) * (z1 - z0) / (x1 - x0) + z0
                    z_ma = max(z_ma, z)
                    z_mi = min(z_mi, z)
        if not z_mi <= z_ma:
            return 0
        return (z_ma - z_mi) * (y_ma - y_mi)
    xs = set()
    xs.update((x for (x, y) in P))
    xs.update((x for (x, z) in Q))
    X = sorted(xs)
    ans = 1
    L = len(X)
    for i in range(L - 1):
        x0 = X[i]
        x1 = X[i + 1]
        s0 = calc(x0)
        s1 = calc((x0 + x1) / 2)
        s2 = calc(x1)
        if s1 > 0:
            ans += (x1 - x0) * (s0 + s2 + 4 * s1) / 6
    return ans

