def check_subsequence_existence(T, test_cases):
    results = []
    for case in test_cases:
        (N, M, B, Z) = case
        yes = M > N - 1
        for i in range(1, N):
            yes = yes and B[i] >= B[i - 1]
        ind = 0
        for i in range(N - 1):
            if not yes:
                break
            R = B[i + 1] - B[i]
            if R == 0:
                while ind < M and Z[ind] != 0:
                    ind += 1
                if ind == M:
                    yes = False
                ind += 1
            else:
                dp = 1
                mod = (1 << R + 1) - 1
                while ind < M and dp >> R & 1 == 0:
                    if Z[ind] <= R:
                        dp |= dp << Z[ind] & mod
                    ind += 1
                if dp >> R & 1 == 0:
                    yes = False
        results.append('YES' if yes else 'NO')
    return results

