def min_deletions_to_equalize_differences(arr, n=None):
    if n is not None:
        n = len(arr)
    if n <= 2:
        return 0
    frequency_dict = {}
    for num in arr:
        if num in frequency_dict:
            frequency_dict[num] += 1
        else:
            frequency_dict[num] = 1
    max_freq = max(frequency_dict.values())
    if max_freq == 1:
        return n - 2
    return n - max_freq

