def count_valid_triples(N):
    """"""
    import math
    nn = int(math.sqrt(N)) + 1
    a = [0] * (100 * N)
    for x in range(1, nn + 1):
        for y in range(1, nn + 1):
            for z in range(1, nn + 1):
                index = (x + y + z) ** 2 - x * y - x * z - y * z
                if index <= N:
                    a[index] += 1
    return a[1:N + 1]
