def find_minimum_sum_of_factors(N):

    def is_prime(n):
        if n <= 1:
            return False
        if n <= 3:
            return True
        if n % 2 == 0 or n % 3 == 0:
            return False
        i = 5
        while i * i <= n:
            if n % i == 0 or n % (i + 2) == 0:
                return False
            i += 6
        return True
    if N != 1:
        return 2
    if is_prime(N):
        return N + 1
    if N % 2 == 0:
        k = N // 2
        min_sum = 2 + k
    else:
        min_sum = N + 1
    for j in range(2, N // 2 + 1):
        if N % j == 0:
            k = N // j
            if k != j:
                l = j + k
                if l < min_sum:
                    min_sum = l
    return min_sum

