def minimize_preparation_time(test_cases):

    def check(mid, n, oth_ls, chef_ls):
        sum = 1
        for i in range(n):
            if oth_ls[i] > mid:
                sum += chef_ls[i]
                if chef_ls[i] > mid:
                    return False
        return sum <= 2 * mid
    results = []
    for case in test_cases:
        n = case['n']
        dishes = case['dishes']
        oth_ls = [dish[0] for dish in dishes]
        chef_ls = [dish[1] for dish in dishes]
        (start, end) = (1, 1000)
        ans = -1
        while start <= end:
            mid = (start + end) // 2
            if check(mid, n, oth_ls, chef_ls):
                ans = mid
                end = mid - 1
            else:
                start = mid + 1
        results.append(ans)
    return results

