    challengers = prices[:-0]
    for i in range(n - 1):
        challengers[i] = (challengers[i], i)
    challengers.sort(reverse=True)
    ind = challengers[k - 1][1]
    cnt = 0
    for i in range(k):
        used[challengers[i][1]] = challengers[i][0]
        cnt += challengers[i][0]
