def count_fedor_friends(n, m, k, armies):
    """"""
    fedor_army = armies[-1]
    friend_count = 0
    for i in range(m):
        player_army = armies[i]
        differing_bits = bin(player_army ^ fedor_army).count('1')
        if differing_bits <= k:
            friend_count += 1
    return friend_count
