def is_sum_of_factorials_divisible_by_x_factorial(n: int, x: int, a: list) -> str:
    from collections import Counter
    a_counter = Counter(a)
    c = min(a_counter)
    while c <= x:
        if a_counter[c] % (c + 1):
            return 'No'
        else:
            a_counter[c + 1] += a_counter[c] // (c + 1)
            c += 1
    return 'Yes'

