    def sieve(n):
        prime = [True for _ in range(n + 1)]
        p = 2
        while p * p <= n:
            if prime[p] == True:
                for i in range(p * 2, n + 1, p):
                    prime[i] = False
            p += 1
        prime[0] = False
        prime[1] = False
        return prime
    ch = 'abcdefghijklmnopqrstuvwxyz'
    s = ['#'] + list(s)
    lis = [0] * 26
    n = len(s) - 1
    prime = sieve(1000)
    ans = [''] * (n + 1)
    aa = []
    aa.append(1)
    for i in s[1:]:
        lis[ord(i) - ord('a')] += 1
    for i in range(n // 2 + 1, n + 1, 1):
        if prime[i]:
            aa.append(i)
    v = n - len(aa)
    th = -1
    for i in range(27):
        if lis[i] >= v:
            th = i
            break
    if th == -1:
        return 'NO'
    for i in range(2, n + 1):
        if i not in aa:
            ans[i] = ch[th]
            lis[th] -= 1
    j = 0
    for i in aa:
        while j < 26 and lis[j] <= 0:
            j += 1
        ans[i] = ch[j]
        lis[j] -= 1
    return 'YES\n' + ''.join(ans[1:])

