def calculate_max_score(N, K, A):
    B = []
    m = max(A)
    M = 2 * N
    count = 0
    if min(A) != 1:
        count = 1
        B.append(1)
        A.append(1)
        N += 1
    sum1 = 0
    sum2 = 0
    A.sort()
    for j in range(N - 1):
        d = A[j + 1] - A[j]
        if A[j + 1] - A[j] > 1:
            for l in range(d - 1):
                B.append(A[j] + 1 + l)
                count += 1
                if count == K:
                    break
        if count == K:
            break
    if count < K:
        for j in range(K - count):
            B.append(m + 1 + j)
    for j in range(K):
        sum1 += m - B[j]
        sum2 += M - B[j]
    sum2 += M - B[K - 1]
    return max(sum1, sum2)

