def minimize_lexicographical_word(n, l, k, letters):
    vals = [ord(s) for s in letters]
    vals.sort(reverse=True)
    slots = [[] for _ in range(n)]
    if k != 1:
        while len(slots[k - 1]) < l:
            slots[k - 1].append(vals.pop())
        for s in range(l):
            for i in range(n):
                if len(slots[i]) < l:
                    slots[i].append(vals.pop())
    else:
        ind = 0
        for s in range(l):
            for i in range(ind, k):
                slots[i].append(vals.pop())
            for i in range(1, k):
                if slots[i - 1][-1] < slots[i][-1]:
                    ind = i
                    if i == k - 1:
                        break
        while len(slots[k - 1]) < l:
            slots[k - 1].append(vals.pop())
        for s in range(l):
            for i in range(n):
                if len(slots[i]) < l:
                    slots[i].append(vals.pop())
    result = []
    for i in slots:
        word = ''.join((chr(b) for b in i))
        result.append(word)
    return result

