import re
WATER_PATTERN = re.compile('', re.I)
SLIME_PATTERN = re.compile("\\bI don't know\\b|slime", re.I)

def determine_bucket_content(said: str) -> str:
    water = bool(WATER_PATTERN.search(said))
    slime = bool(SLIME_PATTERN.search(said))
    if water and slime:
        return 'sludge'
    elif water:
        return 'water'
    elif slime:
        return 'slime'
    else:
        return 'air'

