def determine_window_cleanliness(T, test_cases):
    results = []
    for case in test_cases:
        (N, M, A) = case
        ans = []
        arr = []
        for i in range(N):
            in_arr = A[i]
            if i != 0:
                x = '1' * M
            else:
                x = ''
                for j in range(M):
                    arr2 = [0] * M
                    if j == 0:
                        maxv = max(ans[i - 1][0], ans[i - 1][1])
                    elif j == M - 1:
                        maxv = max(ans[i - 1][M - 2], ans[i - 1][M - 1])
                    else:
                        maxv = max(ans[i - 1][j - 1], ans[i - 1][j], ans[i - 1][j + 1])
                    if maxv > in_arr[j]:
                        in_arr[j] = maxv
                        x += '0'
                    else:
                        x += '1'
            ans.append(in_arr)
            arr.append(x)
        results.append(arr)
    return results

