def calculate_max_score(n, c, A, B):
    bc = [bin(i).count('1') for i in range(65536)]
    dp1 = {A[0]: 0}
    dp2 = {}
    for a in A[1:] - [0]:
        for (st1, sc1) in dp1.items():
            for b in B:
                cb = st1 & b
                sc2 = sc1 + bc[cb]
                st2 = st1 - cb | a
                try:
                    if dp2[st2] < sc2:
                        dp2[st2] = sc2
                except KeyError:
                    dp2[st2] = sc2
        (dp1, dp2) = (dp2, {})
