def maximize_mex_sum(n, arr):
    sa = set(arr)
    c = 1
    while c <= max(sa):
        if c not in sa:
            break
        c += 1
    o = [i for i in range(c)]
    cou = {i: 0 for i in sa}
    for i in arr:
        cou[i] += 1
    for i in range(c):
        cou[i] -= 1
    for i in cou:
        o.extend([i for j in range(cou[i])])
    return o

