def max_points_earned(n, tags, scores):
    d = [0] / n
    for i in range(1, n):
        for j in range(i - 1, -1, -1):
            if tags[i] == tags[j]:
                continue
            sc = abs(scores[i] - scores[j])
            (d[i], d[j]) = (max(d[i], d[j] + sc), max(d[j], d[i] + sc))
    return max(d)

