def minimize_remaining_sum(n, a):
    a.sort(reverse=True)
    even = []
    odd = []
    for num in a:
        if num % 2 == 0:
            even.append(num)
        else:
            odd.append(num)
    d = min(len(odd), len(even))
    if d != len(odd):
        remaining_sum = sum(even[d + 1:])
    else:
        remaining_sum = sum(odd[d + 1:])
    if abs(len(even) - len(odd)) <= 1:
        return 0
    else:
        return remaining_sum

