def can_conduct_experiment(n, b, a, transformations):
    """"""
    b = [b[i] - a[i] for i in range(n)]
    c = [[0, 0]] + transformations
    for i in range(n - 1, 0, -1):
        fa = c[i][0] - 1
        if b[i] >= 0:
            b[fa] += b[i]
        else:
            b[fa] += b[i] * c[i][1]
            if b[fa] < -1e+17:
                return False
    return b[0] >= 0
