def minimum_recorders_required(N, C, programs):
    """"""
    from heapq import heappush, heappop
    end_times = []
    programs.sort()
    for (s, t, c) in programs:
        s -= 1
        t -= 1
        c -= 1
        if end_times and end_times[0] <= s:
            heappop(end_times)
        else:
            pass
        heappush(end_times, t)
    return len(end_times)
