def calculate_outgoing_traffic(commands):
    n = 1
    total_traffic = 0
    for command in commands:
        if command[0] == '+':
            n += 1
        elif command[0] == '-':
            n -= 1
        else:
            sender_and_message = command.split(':', 1)
            if len(sender_and_message) == 2:
                message_length = len(sender_and_message[1])
                total_traffic += message_length * n
    return total_traffic

