        return (2, [0, 1])
    n = 1 << N
    ans = 4
    for i in range(1, int(n / 2) + 1):
        ans = ans * i % mod
    permutation = [0]
    for i in range(1, int(n / 2)):
        if i & 1:
            permutation.extend([i, n >> 1 ^ i])
        else:
            permutation.extend([n >> 1 ^ i, i])
    permutation.append(n >> 1)
    return (ans, permutation)

