def format_indian_currency(amount: str) -> str:
    (A, l, flag, x) = ('', len(amount) - 1, 0, 1)
    while l > 0:
        if flag > 0:
            if x == 2:
                A += amount[l]
                A += ','
                x = 1
            else:
                A += amount[l]
                x += 1
        elif x == 3:
            flag = 1
            x = 1
            A += amount[l]
            A += ','
        else:
            A += amount[l]
            x += 1
        l -= 1
    if A[len(A) - 1] == ',':
        formatted_amount = A[:len(A) - 1][::-1]
    else:
        formatted_amount = A[::-1]
    return formatted_amount

