def is_valid_v_shaped_track(A, N):
    if N % 2 != 0:
        return 'No'
    mid = N // 2
    if A[mid] != 1:
        return 'No'
    constant1 = abs(A[1] - A[0])
    constant2 = abs(A[-1] - A[-2])
    if constant1 == constant2 and constant1 != 0 and (constant2 != 0):
        left = 1
        right = N - 2
        while left < right:
            if abs(A[left] - A[left + 1]) != constant1 or abs(A[right] - A[right - 1]) != constant2:
                return 'No'
            left += 1
            right -= 1
        return 'Yes'
    else:
        return 'No'

