def calculate_max_sum_of_distinct_divisors(N):
    max_sum = 1
    for i in range(1, int(N ** (1 / 2)) + 1):
        if N % i == 0:
            k = N // i
            if k == i:
                max_sum += i
            else:
                max_sum += i + k
    return max_sum

