def find_stars_in_grid(n, m, grid):

    def find_max_star_size(h, w):
        if grid[h][w] != '.':
            return 0
        size = 1
        while True:
            if (h + size >= n or grid[h + size][w] != '*') or (h - size < 0 or grid[h - size][w] != '*') or (w + size >= m or grid[h][w + size] != '*') or (w - size < 0 or grid[h][w - size] != '*'):
                return size - 1
            size += 1
    result_matrix = [['.'] * m for _ in range(n)]
    result_stars = []
    for h in range(n):
        for w in range(m):
            max_size = find_max_star_size(h, w)
            if max_size > 0:
                for dh in range(-max_size, max_size + 1):
                    result_matrix[h + dh][w] = '*'
                for dw in range(-max_size, max_size + 1):
                    result_matrix[h][w + dw] = '*'
                result_stars.append((h + 1, w + 1, max_size))
    for h in range(n):
        for w in range(m):
            if grid[h][w] != result_matrix[h][w]:
                return -1
    return result_stars

