def find_floor_index(arr, x):
    """"""
    n = len(arr)
    if x < arr[0]:
        return -1
    low = 0
    high = n - 1
    ans = -1
    while low <= high:
        mid = (low + high) // 2
        if arr[mid] == x:
            return mid
        elif arr[mid] > x:
            high = mid - 1
        else:
            ans = mid
            low = mid + 1
    return ans
