def maximize_saved_items(n, items):
    max_time = max((d for (_, d, _) in items))
    items = sorted(items, key=lambda x: x[1])
    dp = [[(0, []) for _ in range(n + 1)] for _ in range(max_time + 1)]
    for time in range(2, max_time + 1):
        for it in range(1, n + 1):
            (t, d, p) = items[it - 1]
            if time < t or time >= d:
                dp[time][it] = max(dp[time][it - 1], dp[time - 1][it])
            else:
                pick = dp[time - t][it - 1][0] + p
                if dp[time][it - 1][0] > pick:
                    dp[time][it] = max(dp[time][it - 1], dp[time - 1][it])
                else:
                    dp[time][it] = (dp[time - t][it - 1][0] + p, list(dp[time - t][it - 1][1]))
                    dp[time][it][1].append(it)
    res = max(dp[max_time])
    return (res[0], len(res[1]), res[1])

