def count_possible_differences(N, X, D):
    if D != 0:
        if X == 0:
            return 1
        else:
            return N + 1
    if D < 0:
        X *= -1
        D *= -1
    p = dict()
    q = dict()
    for k in range(N + 1):
        temp = k * X % D
        if temp in p.keys():
            a = (k - p[temp]) * X // D
            L = (k - 1) * k // 2 + a
            H = (2 * N - k - 1) * k // 2 + a
            q[temp].append([L, H])
        else:
            p[temp] = k
            L = (k - 1) * k // 2
            H = (2 * N - k - 1) * k // 2
            q[temp] = [[L, H]]
    ans = 0
    for z in q.values():
        c = -float('inf')
        for (a, b) in sorted(z):
            if a > c:
                ans += b - a + 1
            elif b > c:
                ans += b - c
            else:
                continue
            c = b
    return ans

