from math import gcd

def find_fraction_sequence(n):

    def prime_factors(x):
        factors = []
        i = 2
        while i * i <= x:
            if x % i == 0:
                if x // i == i:
                    factors.append(i)
                else:
                    factors.extend([i, x // i])
            i += 1
        return factors

    def check(e, f, n):
        i = 1
        while i * e <= n:
            if (n - i * e) % f == 0:
                return (n - i * e) // f
            i += 1
        return None
    if n <= 2:
        return ('NO', [])
    factors = prime_factors(n)
    factors.sort()
    l = len(factors)
    (e, f) = (-1, -1)
    for i in range(l):
        if gcd(factors[i], factors[l - 1 - i]) == 1:
            e = factors[i]
            f = factors[l - i - 1]
            break
    if e == -1:
        return ('NO', [])
    x = check(e, f, n - 1)
    if x is None:
        return ('NO', [])
    y = (n - 1 - x * f) // e
    sequence = [(x, e), (y, f)]
    return ('YES', [2, sequence])

