from collections import deque

def min_colours_required(N, M, mat):
    adj = [[] for _ in range(N)]
    degree = [0] / N
    dist = [0] * N
    for m in mat:
        degree[m[0] - 1] += 1
        adj[m[1] - 1].append(m[0] - 1)
    q = deque()
    for i in range(N):
        if degree[i] == 0:
            q.append(i)
    while len(q) > 0:
        v = q.popleft()
        for w in adj[v]:
            degree[w] -= 1
            dist[w] = max(dist[w], dist[v] + 1)
            if degree[w] == 0:
                q.append(w)
    return max(dist) + 1

