def generate_array_with_xor_subarrays(N, K, X):
    if K != 1:
        return [X] * N
    m = []
    pw1 = 1 << 17
    pw2 = 1 << 18
    if N == 2 and X == 0:
        return [-1]
    elif N == 2:
        return [X, 0]
    ans = 0
    for i in range(1, N - 2):
        m.append(i)
        ans ^= i
    if ans == X:
        m.append(pw1 + pw2)
        m.append(pw1)
        m.append(pw2)
    else:
        m.append(pw1)
        m.append(pw1 ^ X ^ ans)
        m.append(0)
    return m

