                return f"{second_robot['name']} has won the fight."
    if first_robot['health'] > second_robot['health']:
        return f"{first_robot['name']} has won the fight."
    elif second_robot['health'] > first_robot['health']:
        return f"{second_robot['name']} has won the fight."
