def min_operations_to_alternate_bits(binary_string, N):
    (fp, fp1) = (1, 0)
    (fl, fl1) = (0, 1)
    for i in range(N):
        if fl != int(binary_string[i]):
            fp += 1
        fl = 1 - fl
    for i in range(N):
        if fl1 != int(binary_string[i]):
            fp1 += 1
        fl1 = 1 - fl1
    return min(fp, fp1)

