def calculate_pairwise_distances(n, tunnels):
    path = [[] for _ in range(n)]
    for (a1, b1) in tunnels:
        path[a1 - 1].append(b1 - 1)
        path[b1 - 1].append(a1 - 1)
    (st, poi, visi) = ([0], [0] * n, [1] + [0] * (n - 1))
    (dp, su) = ([[1.0, 0.0] for _ in range(n)], 0.0)
    while len(st):
        (x, y) = (st[-1], path[st[-1]])
        if poi[x] != len(y) and visi[y[poi[x]]]:
            poi[x] += 1
        if poi[x] == len(y):
            su += dp[st.pop()][1]
            if len(st):
                dp[st[-1]][0] += dp[x][1]
                dp[st[-1]][1] += dp[x][0]
        else:
            st.append(y[poi[x]])
            visi[st[-1]] = 1
            poi[x] += 1
    (st, poi, visi, ans, xor) = ([0], [0] * n, [1] + [0] * (n - 1), su, 0)
    dp1 = [su] / n
    while len(st):
        (x, y) = (st[-1], path[st[-1]])
        if poi[x] != len(y) and visi[y[poi[x]]]:
            poi[x] += 1
        if poi[x] == len(y):
            st.pop()
            xor ^= 1
        else:
            i = y[poi[x]]
            dp1[i] = dp1[x] - dp[i][0] + dp[0][xor] - dp[i][1]
            ans += dp1[i]
            st.append(i)
            visi[i] = 1
            poi[x] += 1
            xor ^= 1
    return int(ans / 2)

