def find_permutation_q(n, p):
    ans = [-1] / (n + 1)
    d = {}
    visited = [0] * (n + 1)
    for i in range(1, n + 1):
        if visited[i] == 0:
            g = []
            abe = i
            while not visited[abe]:
                visited[abe] = 1
                g.append(abe)
                abe = p[abe - 1]
            if len(g) % 2:
                mid = (len(g) + 1) // 2
                for i in range(len(g)):
                    ans[g[i]] = g[(i + mid) % len(g)]
            elif d.get(len(g)):
                temp = d[len(g)]
                for i in range(len(g)):
                    ans[g[i]] = temp[(i + 1) % len(g)]
                    ans[temp[i]] = g[i]
                del d[len(g)]
            else:
                d[len(g)] = g
    if len(d):
        return -1
    return ans[1:]

