def count_balanced_binary_strings(N: int) -> int:
    """"""
    if N == 0:
        return 1
    dp = [0] * (N + 1)
    dp[0] = 1
    dp[1] = 1
    mod = 1000000007
    for i in range(2, N + 1):
        for j in range(i):
            dp[i] = (dp[i] + dp[j] * dp[i - j - 1]) % mod
    return dp[N]
