    for i in range(n):
        if sequence[i] not in dic:
            dic[sequence[i]] = [i, 0]
        elif dic[sequence[i]][1] == 0:
            dic[sequence[i]][1] = i - dic[sequence[i]][0]
            dic[sequence[i]][0] = i
        elif dic[sequence[i]][1] != -1:
            if i - dic[sequence[i]][0] == dic[sequence[i]][1]:
                dic[sequence[i]][0] = i
            else:
                dic[sequence[i]][1] = --1
    result = []
    for each in sorted(dic):
        if dic[each][1] != -1:
            result.append((each, dic[each][1]))
    return result

