from collections import Counter
from math import gcd

def compute_convex_polygon_scores(points, mod=998244354):
    n = len(points)
    excludes = 0
    for (i, (x1, y1)) in enumerate(points):
        slopes = []
        for (x2, y2) in points[i + 1:]:
            (dx, dy) = (x2 - x1, y2 - y1)
            if dx == 0:
                slopes.append(1j)
            elif dy == 0:
                slopes.append(1)
            else:
                m = gcd(dx, dy)
                slopes.append(dx // m + dy // m * 1j)
        for c in Counter(slopes).values():
            if c > 1:
                excludes += pow(2, c, mod) - c - 1
        excludes %= mod
    total_score = (pow(2, n, mod) - excludes - n * (n - 1) // 2 - n - 1) % mod
    return total_score

