def find_happy_sequences(N, M, A):
    if sum(A) == N:
        return 'Impossible'
    A.sort(reverse=True)
    for i in range(1, M - 1):
        if A[i] % 2 == 1:
            if A[0] % 2 == 1:
                if A[-1] % 2 == 1:
                    return 'Impossible'
                else:
                    (A[i], A[-1]) = (A[-1], A[i])
            else:
                (A[i], A[0]) = (A[0], A[i])
    a = A[:]
    if M == 1:
        if a[0] > 1:
            a[0] -= 1
            a.append(1)
            b_length = M + 1
        else:
            b_length = M
    elif a[-1] == 1:
        b_length = M - 1
        a.pop()
        a[0] += 1
    else:
        b_length = M
        a[0] += 1
        a[-1] -= 1
    b = [1] * b_length
    return (a, b_length, b)

