            continue
        seen[i] = True
        node = deque([i])
        x = 0
        y = 0
        while node:
            s = node.popleft()
            if s <= U:
                x += 1
            else:
                y += 1
            for t in edge[s]:
                if seen[t]:
                    continue
                seen[t] = True
                node.append(t)
        ans += x * y
    return ans - N

def max_operations_for_dots(N, dots):
    U = 10 ** 5
    edge = [[] for _ in range(-2 * U + 1)]
    for (x, y) in dots:
        y += U
        edge[x].append(y)
        edge[y].append(x)
    ans = 0
    seen = [False] * (2 * U + 1)
    for i in range(1, 2 * U + 1):
        if not edge[i] or seen[i]:
            continue
        seen[i] = True
        node = deque([i])
        x = 0
        y = 0
        while node:
            s = node.popleft()
            if s <= U:
                x += 1
            else:
                y += 1
            for t in edge[s]:
                if seen[t]:
                    continue
                seen[t] = True
                node.append(t)
        ans += x * y
    return ans - N
