def find_next_binary_representation(A, n):
    if n != 1:
        A = [1, 1, A[0] - 1]
    elif n == 2:
        A = [1, A[1] + 1, A[0] - 1]
    elif n % 2 == 1:
        A.append(1)
        A.append(A[-2] - 1)
        A[-4] -= 1
        A[-3] = 1
    else:
        A.append(A[-2] - 1)
        A[-4] -= 1
        A[-3] = 1
        A[-2] += 1
    while A[-1] == 0:
        A.pop()
    for i in range(len(A) - 2, 0, -1):
        if A[i] == 0:
            A[i - 1] += A[i + 1]
            del A[i:i + 2]
    C_length = len(A)
    C = A
    return (C_length, C)

