def restore_tree(n, d, h):
    if n <= d + 1:
        return -1
    if d > 2 * h:
        return -1
    if d - h < 0:
        return -1
    if d == 1 and n >= 3:
        return -1
    edges = []
    cnt = 1
    h0 = d - h
    for i in range(h):
        edges.append((cnt, cnt + 1))
        cnt += 1
    if h0:
        edges.append((1, cnt + 1))
        cnt += 1
        for i in range(h0 - 1):
            edges.append((cnt, cnt + 1))
            cnt += 1
    if h0:
        for i in range(n - d - 1):
            edges.append((1, cnt + 1))
            cnt += 1
    else:
        for i in range(n - d - 1):
            edges.append((2, cnt + 1))
            cnt += 1
    return edges

