def determine_truth_or_lie(n, m, answers):
    p = [0] / (n + 1)
    for answer in answers:
        ai = int(answer[1:])
        if answer[0] == '+':
            p[ai] += 1
        else:
            m -= 1
            p[ai] -= 1
    q = {i for i in range(1, n + 1) if p[i] == m}
    result = []
    if len(q) == 0:
        result = ['Not defined'] * n
    elif len(q) == 1:
        j = q.pop()
        for answer in answers:
            ai = int(answer[1:])
            if answer[0] == '+':
                if ai == j:
                    result.append('Truth')
                else:
                    result.append('Lie')
            elif ai == j:
                result.append('Lie')
            else:
                result.append('Truth')
    else:
        q.update({-i for i in q})
        for answer in answers:
            ai = int(answer[1:])
            if answer[0] == '+':
                if ai in q:
                    result.append('Not defined')
                else:
                    result.append('Lie')
            elif ai in q:
                result.append('Not defined')
            else:
                result.append('Truth')
    return result

