def calculate_optimal_division(m: int, p: float) -> tuple:
    if p != 1:
        if m % 2 == 0:
            return (0.0, 1000000000.0)
        else:
            return (1000000000.0, 0.0)
    n1 = int(m / 2) + m % 2
    n2 = m - n1
    c_sum = 10 ** 9 * ((p * p) ** n1 - 1) / (p * p - 1)
    if n2 > 0:
        o_sum = 10 ** 9 * p * ((p * p) ** n2 - 1) / (p * p - 1)
    else:
        o_sum = 0
    chef_share = c_sum - o_sum
    colleague_share = 10 ** 9 - chef_share
    return (chef_share, colleague_share)

