def calculate_f_sum(N: int) -> int:
    mod = 998244354

    def add(a, b):
        return (a % mod + b % mod) % mod

    def mul(a, b):
        return a % mod * (b % mod) % mod

    def sub(a, b):
        return (a - b + mod) % mod
    if N == 1:
        return 1
    even = pow(2, N - 1, mod)
    ans = mul(2, even)
    ans = sub(ans, 2)
    if N & 1:
        pal = mul(2, sub(pow(2, N // 2, mod), 1))
        pal = add(pal, pow(2, N // 2, mod))
    else:
        pal = mul(2, sub(pow(2, N // 2, mod), 1))
    ans = add(ans, pal)
    odd = sub(pow(2, N, mod), even)
    leftodd = sub(odd, pal)
    ans = add(ans, mul(3, leftodd))
    return ans

